import { Component } from '@angular/core';
import { HttpClient, HttpClientModule } from '@angular/common/http';

@Component({
  selector: 'app-view-pokemon',
  templateUrl: './view-pokemon.component.html',
  styleUrls: ['./view-pokemon.component.css']
})
export class ViewPokemonComponent {
  constructor(private http: HttpClient) { 

  }


  fetchPokemon() {
    this.http.get('https://pokeapi.co/api/v2/pokemon').subscribe((data:any) => {
      console.log(data);
      // Aquí puedes procesar los datos de la respuesta según tus necesidades
    });
  }
}
