    <ul class="list-group">
      <li *ngFor="let pokemon of pokemon" class="list-group-item">
        <a href="javascript:void(0);" (click)="getPokemonHability(pokemon.url)">{{ pokemon.name }}</a>
        <div *ngIf="datosPokemon && datosPokemon.name === pokemon.name">
            <ul>
                <li *ngFor="let ability of abilities">{{ ability.ability.name }}</li>
            </ul>
                <label class="text-success">peso:</label><label class="text-success">{{weight}}</label>
                <br>
                <label class="text-success">altura:</label><label class="text-success">{{height}}</label>
                <br>
                <img [src]="imgPokemin" alt="Pokemon Sprite">
                <br>
                <label class="text-warning">Apariciones en otro titulo</label>
                <ul>
                    <li *ngFor="let games of game_indices">{{ games.version.name }}</li>
                </ul>
                <br>
                <label class="text-warning">Tipo pokemon</label>
                <ul>
                    <li *ngFor="let type of types">{{ type.type.name }}</li>
                </ul>
          </div>
          <button (click)="agregarFav(pokemon)" [disabled]="isFavorite(pokemon.name)" >Agregar a favoritos</button>
      </li>
    </ul>
    <div class="card" style="width: 18rem;" *ngFor="let pokemon of pokemons">
        <img class="card-img-top" [src]="pokemon.image" alt="Pokemon Sprite">
        <div class="card-body">
          <h5 class="card-title">{{ pokemon.name }}</h5>
          <div *ngIf="datosPokemon && datosPokemon.name === pokemon.name">
            <ul>
              <li *ngFor="let ability of abilities">{{ ability.ability.name }}</li>
            </ul>
            <label class="text-success">peso:</label><label class="text-success">{{weight}}</label>
            <br>
            <label class="text-success">altura:</label><label class="text-success">{{height}}</label>
            <br>
            <img [src]="imgPokemon" alt="Pokemon Sprite">
            <br>
            <label class="text-warning">Apariciones en otro título</label>
            <ul>
              <li *ngFor="let game of game_indices">{{ game.version.name }}</li>
            </ul>
            <br>
            <label class="text-warning">Tipo pokemon</label>
            <ul>
              <li *ngFor="let type of types">{{ type.type.name }}</li>
            </ul>
            <button (click)="agregarFav(pokemon.name)" [disabled]="isFavorite(pokemon.name)" class="btn btn-primary">Agregar a favoritos</button>
          </div>
        </div>
      </div>
    <div *ngFor="let pokemon of favoritePokemons">
        <h3>{{ pokemon.name }}</h3>
        <button (click)="eliminarFav(pokemon)" [disabled]="isFavorite(pokemon.name)">Eliminar de favoritos</button>

      </div>
  </div>
